import { useQuery } from "@tanstack/react-query";
import React from "react";
import { getWeakDay } from "services/onSite";

export const weakDays = {
  monday: "دوشنبه",
  tuesday: "سه شنبه",
  wednesday: "چهارشنبه",
  thursday: "پنج شنبه",
  friday: "جم",
  saturday: "شنبه",
  sunday: "یکشنبه",
};

export default function useGetWeakDay() {
  const { data } = useQuery({
    queryKey: ["get-weakDay"],
    queryFn: getWeakDay,
  });
  return { data };
}
