import { useQuery } from "@tanstack/react-query";
import React, { useEffect } from "react";
import { getWeakDay } from "services/onSite";

export const weakDays = {
  monday: "دوشنبه",
  tuesday: "سه شنبه",
  wednesday: "چهارشنبه",
  thursday: "پنج شنبه",
  friday: "جمعه",
  saturday: "شنبه",
  sunday: "یکشنبه",
};

export default function useGetWeakDay() {
  const { data } = useQuery({
    queryKey: ["get-weakDay"],
    queryFn: getWeakDay,
  });

  useEffect(() => {} , [data])
  return { data };
}
