const genStepsRTLStyle = token => {
  const {
    componentCls
  } = token;
  return {
    ["&".concat(componentCls, "-rtl")]: {
      direction: 'rtl',
      ["".concat(componentCls, "-item")]: {
        '&-subtitle': {
          float: 'left'
        }
      },
      // nav
      ["&".concat(componentCls, "-navigation")]: {
        ["".concat(componentCls, "-item::after")]: {
          transform: 'rotate(-45deg)'
        }
      },
      // vertical
      ["&".concat(componentCls, "-vertical")]: {
        ["> ".concat(componentCls, "-item")]: {
          '&::after': {
            transform: 'rotate(225deg)'
          },
          ["".concat(componentCls, "-item-icon")]: {
            float: 'right'
          }
        }
      },
      // progress-dot
      ["&".concat(componentCls, "-dot")]: {
        ["".concat(componentCls, "-item-icon ").concat(componentCls, "-icon-dot, &").concat(componentCls, "-small ").concat(componentCls, "-item-icon ").concat(componentCls, "-icon-dot")]: {
          float: 'right'
        }
      }
    }
  };
};
export default genStepsRTLStyle;
