import { useQuery } from "@tanstack/react-query";
import dayjs from "dayjs";
import React, { useEffect, useState } from "react";
import { getWeakDay } from "services/onSite";

// Mapping week days to Persian names
export const weakDays: Record<string, string> = {
  monday: "دوشنبه",
  tuesday: "سه شنبه",
  wednesday: "چهارشنبه",
  thursday: "پنج شنبه",
  friday: "جمعه",
  saturday: "شنبه",
  sunday: "یکشنبه",
};

// Type for the options array
export interface IOption {
  value: number;
  label: string;
}

// Custom hook to fetch and format the weak days
export default function useGetWeakDay() {
  const [days, setDays] = useState<IOption[]>([]);

  // Fetch weak days from the server
  const { data } = useQuery({
    queryKey: ["get-weakDay"],
    queryFn: getWeakDay,
  });

  // Set the Persian formatted days when data is available
  useEffect(() => {
    if (data) {
      // Get the current date formatted in Persian once
      const formattedDate = dayjs().locale("fa").format("D MMMM");

      // Map over the data to create the new array
      const newData: IOption[] = data.map(
        (item: { id: number; name: string }) => ({
          value: item.id,
          label: `${weakDays[item.name.toLowerCase()]} ${formattedDate}`, // Use formatted date
        })
      );

      setDays(newData);
    }
  }, [data]);

  return { days };
}
