import { useQuery } from "@tanstack/react-query";
import dayjs from "dayjs";
import moment from "jalali-moment";
import React, { useEffect, useState } from "react";
import { getWeakDay } from "services/onSite";

export const weakDays = {
  monday: "دوشنبه",
  tuesday: "سه شنبه",
  wednesday: "چهارشنبه",
  thursday: "پنج شنبه",
  friday: "جمعه",
  saturday: "شنبه",
  sunday: "یکشنبه",
};
export interface IOption {
  value: number;
  label: string;
}

export const months = {
    1 : "فروردین",
    2 :"اردیبهشت",
    3 : "خرداد",
    4 : "تیر",
    5 : "",
    6 : "",
    7 : "",
    8 : "",
    9 : "",
    10:  "",
    11 : "",
    12 : ""
}
export default function useGetWeakDay() {
  const [days, setDays] = useState<IOption[]>([]);
  const { data } = useQuery({
    queryKey: ["get-weakDay"],
    queryFn: getWeakDay,
  });

  useEffect(() => {
    if (data) {
      const newData: IOption[] = [];
      data.map((item) => {
        newData.push({
          value: item.id,
          //@ts-ignore
          label: `${weakDays[item.name]} ${moment(item.date, "YYYY-MM-DD")
            .locale("fa")
            .format("MM D")}`,
        });
      });
      setDays(newData);
    }
  }, [data]);
  return { days };
}
