import { useQuery } from "@tanstack/react-query";
import dayjs from "dayjs";
import React, { useEffect, useState } from "react";
import { getWeakDay } from "services/onSite";

export const weakDays = {
  monday: "دوشنبه",
  tuesday: "سه شنبه",
  wednesday: "چهارشنبه",
  thursday: "پنج شنبه",
  friday: "جمعه",
  saturday: "شنبه",
  sunday: "یکشنبه",
};
export interface IOption {
  value: number;
  label: string;
}
export default function useGetWeakDay() {
  const [days, setDays] = useState<IOption[]>([]);
  const { data } = useQuery({
    queryKey: ["get-weakDay"],
    queryFn: getWeakDay,
  });

  useEffect(() => {
    if (data) {
      const newData: IOption[] = [];
      data.map((item) => {
        //@ts-ignore
        newData.push({ value: item.id, label: `${weakDays[item.name]} ${dayjs().locale("fa").format('D MMMM')}` });
      });
      setDays(newData);
    }
  }, [data]);
  return { days };
}
